INSERT INTO `categorie` (`id_categoria`, `nome_cat`, `descrizione`, `immagine`, `alt`) VALUES
(1, 'Balance', 'Stai cercando un programma di allenamento dove equilibrio, flessibilità e mobilità sono le parole chiave? Scegli tra i corsi <span lang="en">Balance</span> quello più adatto a te.', 'balance-main.jpg', 'equilibrio'),
(2, 'Cycle', '<span lang="en">FitnessCenter</span> ha studiato un innovativo programma <span lang="en">fitness & cycling</span> che unisce esercizi ad alta intensità a musica e ritmo incalzante. Scegli tra i corsi <span lang="en">Cycle</span> quello più adatto a te.', 'cycle-main.jpg', 'ciclismo'),
(3, 'Dance', 'Per coniugare divertimento, musica e forma fisica e rilassarti scaricando le tensioni giornaliere. Scegli tra i corsi <span lang="en">Dance</span> quello più adatto a te.', 'dance-main.jpg', 'danza'),
(4, 'Functional', 'Programmi di allenamento studiati per rendere il tuo corpo più forte, agile, scattante e coordinato. Scegli tra i corsi <span lang="en">Functional</span> quello più adatto a te.', 'functional-main.jpg', 'funzionale'),
(5, 'Running', 'Corri libero all’aria aperta durante le nostre classi stagionali in alcune delle città più belle d’Italia.', 'running-main.jpg', 'corsa'),
(6, 'Strenght', 'Scolpisci il tuo corpo donandogli tonicità e flessibilità con dei programmi di allenamento specifici per ogni esigenza. Scegli tra i corsi <span lang="en">Strength</span> quello più adatto a te.', 'strength-main.jpg', 'forza'),
(7, 'Water', 'Metti il turbo alla tua voglia di movimento, caricati e sprigiona le energie in acqua. Scegli tra i corsi <span lang="en">Water</span> quello che fa per te, per un\'esperienza di allenamento completa.', 'water-main.jpg', 'acquatici'),
(8, 'Yoga', 'Sincronizza le tue posizioni al ritmo del tuo respiro e scegli la pratica della disciplina millenaria più adatta a te per vivere un\'esperienza irrinunciabile. Scegli tra i corsi Yoga quello più adatto a te.', 'yoga-main.jpg', 'yoga');

